# 1. Function with multiple results
def operacion(a, b):
    suma = a + b
    resta = a - b
    multiplicacion = a * b
    return suma, resta, multiplicacion

# 2. Leap year logic (using % and and/or)
def es_bisiesto(ano):
    if (ano % 4 == 0) and (ano % 100 != 0) or (ano % 400 == 0):
        return "Es bisiesto"
    else:
        return "No es bisiesto"

# 3. Discount logic
def precio_final(precio):
    if precio >= 100:
        descuento = precio * 0.10
        total = precio - descuento
        return total
    else:
        return precio

